import React, { useState, useEffect } from "react";
import { domain } from "../config";

const Menu = () => {
  const [list, setList] = useState([]);
  useEffect(() => {
    setList((array) => {
      return [
        ...array,
        {
          id: 35,
          name: "salmon roll",
          description: "good!",
          price: 35,
          active: false,
        },
      ];
    });
    // try {
    //   //const doWork = await fetch(`${domain}/`);
    // } catch (err) {
    //   throw err;
    // }
  }, []);
  return (
    <div>
      <div>Menu Page</div>
      <div>
        <form>
          <table style={{ width: "100%" }}>
            <tbody>
              <tr>
                <th>No.</th>
                <th>Name</th>
                <th>Description</th>
                <th>Price</th>
                <th>Active</th>
                <th></th>
                <th></th>
              </tr>
              {list.map((item) => {
                  let activeSelect = ""
                  if(item.active){
                    activeSelect = <select><option selected>yes</option><option>No</option></select>
                  }else{
                    activeSelect = <select><option>yes</option><option selected>No</option></select>
                  }
                return (
                  <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.name}</td>
                    <td>{item.description}</td>
                    <td>{item.price}</td>
                    <td>{activeSelect}</td>
                    <td>Update</td>
                    <td>Delete</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </form>
      </div>
    </div>
  );
};

export default Menu;
