import React, { useState, useEffect } from "react";
import { domain } from "../config";

const Category = () => {
  const [list, setList] = useState([]);

  function onChangeHandler(event) {
    let indexOfEvent = event.target.getAttribute("akey");
    console.log(`indexOfEvent = ${indexOfEvent}`);
    let currentObject = list[indexOfEvent];
    console.log(`currentObject = ${currentObject}`);
    let chosenName = event.target.name;
    currentObject[chosenName] = event.target.value;
    //console.log(currentObject)
    setList((array) => {
      console.log("1", array);
      array.splice(indexOfEvent, 1, currentObject);
      return [...array];
    });
  }

  function updateHandler(event) {
      console.log(list)
    fetch(`${domain}admin/category/update`, {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
      body: list
    });
  }

  function deleteHandler(event) {}

  useEffect(() => {
    fetch(`${domain}admin/category/get`, {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setList(data);
      });
  }, []);
  return (
    <div>
      <div>Category Page</div>
      <div>
        <form>
          <table style={{ width: "100%" }}>
            <tbody>
              <tr>
                <th>No.</th>
                <th>Name</th>
                <th>Order</th>
                <th></th>
                <th></th>
              </tr>
              {list.map((item) => {
                return (
                  <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>
                      <input
                        type="text"
                        akey={item.count}
                        value={item.name}
                        onChange={onChangeHandler}
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        akey={item.count}
                        value={item.order}
                        onChange={onChangeHandler}
                      />
                    </td>
                    <td>
                      <input
                        type="button"
                        akey={item.count}
                        value="Update"
                        onClick={updateHandler}
                      />
                    </td>
                    <td>
                      <input
                        type="button"
                        akey={item.count}
                        value="Delete"
                        onClick={deleteHandler}
                      />
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </form>
      </div>
    </div>
  );
};

export default Category;
