import React, { useState, useEffect } from "react";
import { domain } from "../config";

const Menu = () => {
  const [list, setList] = useState([]);
  let count = 0

  function handleChange(event){
    let indexOfEvent = event.target.getAttribute('akey')
    let currentObject = list[indexOfEvent];
    let chosenName = event.target.name
    currentObject[chosenName] = event.target.value
    //console.log(currentObject)
    setList((array)=>{
        console.log(array)
        array.splice(indexOfEvent, 1, currentObject)
        return array
    })
    console.log(list)


  }
  useEffect(() => {
    setList((array) => {
        count++
      return [
        ...array,
        {
          count: count,
          id: 35,
          name: "salmon roll",
          description: "good!",
          price: 35,
          active: false,
        }
      ];
    });
    // try {
    //   //const doWork = await fetch(`${domain}/`);
    // } catch (err) {
    //   throw err;
    // }
  }, []);
  return (
    <div>
      <div>Menu Page</div>
      <div>
        <form>
          <table style={{ width: "100%" }}>
            <tbody>
              <tr>
                <th>No.</th>
                <th>Name</th>
                <th>Description</th>
                <th>Price</th>
                <th>Active</th>
                <th></th>
                <th></th>
              </tr>
              {list.map((item) => {
                  let activeSelect = ""
                  if(item.active){
                    activeSelect = <select><option selected value='true'>yes</option><option value='false'>No</option></select>
                  }else{
                    activeSelect = <select><option  value='true'>yes</option><option selected value='false'>No</option></select>
                  }
                return (
                  <tr key={item.id}>
                    <td><input akey={item.count-1} type="text" name="id" required value={item.count} onChange={handleChange}/></td>
                    <td><input type="text" name="name" required value={item.name}/></td>
                    <td><input type="text" name="description" required value={item.description}/></td>
                    <td><input type="text" name="price" required value={item.price}/></td>
                    <td>{activeSelect}</td>
                    <td>Update</td>
                    <td>Delete</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </form>
      </div>
    </div>
  );
};

export default Menu;
