import React, { useState, useEffect } from "react";
import { domain } from "../config";

const Category = () => {
  const [list, setList] = useState([]);
  let count = 0;

  function onChangeHandler(event) {
    let indexOfEvent = event.target.getAttribute("akey");
    console.log(`indexOfEvent = ${indexOfEvent}`);
    let currentObject = list[indexOfEvent];
    console.log(`currentObject = ${currentObject}`);
    let chosenName = event.target.name;
    currentObject[chosenName] = event.target.value;
    //console.log(currentObject)
    setList((array) => {
      console.log("1", array);
      array.splice(indexOfEvent, 1, currentObject);
      return [...array];
    });
  }

  async function updateHandler(event) {
    let targetOfList = event.target.getAttribute("akey");
    console.log(list[targetOfList]);

    const doWork = await fetch(`${domain}admin/category/update`, {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(list[targetOfList]),
    });
    window.location.reload();
  }

  async function deleteHandler(event) {
    let targetOfList = event.target.getAttribute("akey");
    console.log(list[targetOfList]);

    const doWork = await fetch(`${domain}admin/category/update`, {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(list[targetOfList]),
    });
    window.location.reload();
  }


  useEffect(() => {
    fetch(`${domain}admin/category/get`, {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setList(data);
      });
  }, []);
  return (
    <div>
      <div>Category Page</div>
      <div>
        <form>
          <table style={{ width: "100%" }}>
            <tbody>
              <tr>
                <th>No.</th>
                <th>Name</th>
                <th>Sort</th>
                <th></th>
                <th></th>
              </tr>
              {list.map((item) => {
                count++;
                return (
                  <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>
                      <input
                        type="text"
                        akey={count - 1}
                        name="name"
                        value={item.name}
                        required
                        onChange={onChangeHandler}
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        akey={count - 1}
                        name="sort"
                        value={item.sort}
                        required
                        onChange={onChangeHandler}
                      />
                    </td>
                    <td>
                      <input
                        type="button"
                        akey={count - 1}
                        value="Update"
                        onClick={updateHandler}
                      />
                    </td>
                    <td>
                      <input
                        type="button"
                        akey={count - 1}
                        value="Delete"
                        onClick={deleteHandler}
                      />
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <div>Add</div>
          <table style={{ width: "100%" }}>
              <tbody>
                  <tr><th>Name</th><th>Sort</th></tr>
                  <tr><td><input type="text" /></td><td><input type="text" /></td></tr>
              </tbody>
          </table>
        </form>
      </div>
    </div>
  );
};

export default Category;
