import React, { useState, useEffect } from "react";

const Category = () => {
  const [list, setList] = useState([]);

  function onChangeHandler(event) {
    let indexOfEvent = event.target.getAttribute("akey");
    console.log(`indexOfEvent = ${indexOfEvent}`);
    let currentObject = list[indexOfEvent];
    console.log(`currentObject = ${currentObject}`);
    let chosenName = event.target.name;
    currentObject[chosenName] = event.target.value;
    //console.log(currentObject)
    setList((array) => {
      console.log("1", array);
      array.splice(indexOfEvent, 1, currentObject);
      return [...array];
    });
  }

  function updateHandler(event) {}

  function deleteHandler(event) {}

  useEffect(() => {
    setList([
      {
        count: 1,
        id: 1,
        name: "Roll",
        order: 1,
      },
    ]);
  }, []);
  return (
    <div>
      <div>Category Page</div>
      <div>
        <form>
          <table style={{ width: "100%" }}>
            <tbody>
              <tr>
                <th>No.</th>
                <th>Name</th>
                <th>Order</th>
                <th></th>
                <th></th>
              </tr>
              {list.map((item) => {
                return (
                  <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>
                      <input
                        type="text"
                        akey={item.count}
                        value={item.name}
                        onChange={onChangeHandler}
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        akey={item.count}
                        value={item.order}
                        onChange={onChangeHandler}
                      />
                    </td>
                    <td>
                      <input
                        type="button"
                        akey={item.count}
                        value="Update"
                        onClick={updateHandler}
                      />
                    </td>
                    <td>
                      <input
                        type="button"
                        akey={item.count}
                        value="Delete"
                        onClick={deleteHandler}
                      />
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </form>
      </div>
    </div>
  );
};

export default Category;
