import React, { useState, useEffect } from "react";
import { domain } from "../config";

const MenuUpdate = () => {
  const [list, setList] = useState([]);
  const [categoryOptions, setCategoryOptions] = useState();
  let count = 0;

  function handleChange(event) {
    let indexOfEvent = event.target.getAttribute("akey");
    console.log(`indexOfEvent = ${indexOfEvent}`);
    let currentObject = list[indexOfEvent];
    console.log(`currentObject = ${currentObject}`);
    let chosenName = event.target.name;
    currentObject[chosenName] = event.target.value;
    //console.log(currentObject)
    setList((array) => {
      console.log("1", array);
      array.splice(indexOfEvent, 1, currentObject);
      return [...array];
    });
    //console.log(list)
  }
  useEffect(() => {
      console.log(location.item)
    fetch(`${domain}admin/menu_update/get`, {
        method: "POST",
        mode: "cors",
        cache: "no-cache",
        credentials: "same-origin",
        headers: {
          "Content-Type": "application/json",
        },
      })
        .then((res) => res.json())
        .then((data) => {
          console.log(data);
          setList(data);
        });
  }, []);
  return (
    <div>
      <div>Menu Update Page</div>
      <div>
        <form>
          <table style={{ width: "100%" }}>
            <tbody>
              <tr>
                <th>No.</th>
                <th>Category</th>
                <th>Name</th>
                <th>Description</th>
                <th>Price</th>
                <th>Order</th>
                <th>Image</th>
                <th>Active</th>
                <th></th>
                <th></th>
              </tr>
              {list.map((item) => {
                let activeSelect = "";
                if (item.active) {
                  activeSelect = (
                    <select
                      akey={item.count - 1}
                      name="active"
                      onChange={handleChange}
                    >
                      <option value="true">yes</option>
                      <option value="false">No</option>
                    </select>
                  );
                } else {
                  activeSelect = (
                    <select
                      akey={item.count - 1}
                      name="active"
                      onChange={handleChange}
                    >
                      <option value="true">yes</option>
                      <option selected value="false">
                        No
                      </option>
                    </select>
                  );
                }
                return (
                  <tr key={item.id}>
                    <td>{item.count}</td>
                    <td>
                      <select
                        akey={item.count - 1}
                        name="category"
                        onChange={handleChange}
                        value={item.category}
                      >
                        {categoryOptions}
                      </select>
                    </td>
                    <td>
                      <input
                        akey={item.count - 1}
                        type="text"
                        name="name"
                        required
                        value={item.name}
                        onChange={handleChange}
                      />
                    </td>
                    <td>
                      <input
                        akey={item.count - 1}
                        type="text"
                        name="description"
                        required
                        value={item.description}
                        onChange={handleChange}
                      />
                    </td>
                    <td>
                      <input
                        akey={item.count - 1}
                        type="text"
                        name="price"
                        required
                        value={item.price}
                        onChange={handleChange}
                      />
                    </td>
                    <td>
                      <input
                        akey={item.count - 1}
                        type="text"
                        name="order"
                        required
                        value={item.order}
                        onChange={handleChange}
                      />
                    </td>
                    <td>
                        <input 
                            akey={item.count - 1}
                            type="file"
                            name="image"
                            onChange={(event)=>{console.log(event.target.files[0].name)}}
                        />
                    </td>
                    <td>
                      <select
                        akey={item.count - 1}
                        name="active"
                        onChange={handleChange}
                        value={item.active}
                      >
                        <option value="true">yes</option>
                        <option value="false">No</option>
                      </select>
                    </td>
                    <td>Update</td>
                    <td>Delete</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </form>
      </div>
    </div>
  );
};

export default MenuUpdate;
