import React, { useState, useEffect } from "react";
import { domain } from "../config";

const Hours = () => {
  const [schedule, setSchedule] = useState([]);
  let count = 0;

  useEffect(() => {
    fetch(`${domain}admin/hours/get`, {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setSchedule(data);
      });

    console.log(schedule);
  }, []);

  function onChangeHandler(event){
    let theIndex = event.target.getAttribute("akey")
    let selectedIndex = schedule[theIndex]
    selectedIndex[event.target.name] = event.target.value
    setSchedule((array) => {
      console.log("1", array);
      array.splice(theIndex, 1, selectedIndex);
      return [...array];
    });

  }

  async function updateHandler() {
    const doWork = await fetch(`${domain}admin/hours/update`, {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(schedule),
    });
    window.location.reload();

  }
  return (
    <div>
      <div>Category Page</div>
      <div>
        <form>
          <table style={{ width: "100%" }}>
            <tbody>
              <tr>
                <th></th>
                <th>Open time</th>
                <th>Close time</th>
              </tr>
              {schedule.map((item) => {
                count++
                return (
                  <tr key={item.id}>
                    <td>{item.date}</td>
                    <td>
                      <input akey={count-1} type="text" name="open" value={item.openhour} onChange={onChangeHandler}/>
                    </td>
                    <td>
                      <input akey={count-1} type="text" name="close" value={item.closehour} onChange={onChangeHandler}/>
                    </td>
		    <td>
			<select akey={count-1} name="status" value={item.status} onChange={onChangeHandler} ><option value={1}>Open</option><option value={0}>Closed</option></select>
		    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          <div>
            <input type="button" value="UPDATE" onClick={updateHandler} />
          </div>
        </form>
      </div>
    </div>
  );
};

export default Hours;
