import React, { useState, useEffect } from "react";
import { domain } from "../config";

const Menu = () => {
  const [list, setList] = useState([]);
  const [categoryOptions, setCategoryOptions] = useState();
  let count = 0;

  function handleChange(event) {
    let indexOfEvent = event.target.getAttribute("akey");
    console.log(`indexOfEvent = ${indexOfEvent}`);
    let currentObject = list[indexOfEvent];
    console.log(`currentObject = ${currentObject}`);
    let chosenName = event.target.name;
    currentObject[chosenName] = event.target.value;
    //console.log(currentObject)
    setList((array) => {
      console.log("1", array);
      array.splice(indexOfEvent, 1, currentObject);
      return [...array];
    });
    //console.log(list)
  }
  useEffect(() => {
    console.log("useEffect");
    setList((array) => {
      count++;
      return [
        ...array,
        {
          count: count,
          id: 35,
          category: 3,
          name: "salmon roll",
          description: "good!",
          price: 35,
          sort: 1,
          active: false,
        },
        {
          count: 2,
          id: 36,
          category: 3,
          name: "egg roll",
          description: "good!",
          price: 20,
          sort: 2,
          active: true,
        },
      ];
    });
    //get Category and make option lists
    setCategoryOptions(
      <>
        <option value="roll">roll</option>
        <option value="roll">drink</option>
      </>
    );
    // try {
    //   //const doWork = await fetch(`${domain}/`);
    // } catch (err) {
    //   throw err;
    // }
  }, []);
  return (
    <div>
      <div>Menu Page</div>
      <div>
        <form>
          <table style={{ width: "100%" }}>
            <tbody>
              <tr>
                <th>No.</th>
                <th>Category</th>
                <th>Name</th>
                <th>Description</th>
                <th>Price</th>
                <th>Order</th>
                <th>Image</th>
                <th>Active</th>
                <th></th>
                <th></th>
              </tr>
              {list.map((item) => {
                return (
                  <tr key={item.id}>
                    <td>{item.count}</td>
                    <td>{item.category}</td>
                    <td>{item.name}</td>
                    <td>{item.description}</td>
                    <td>{item.price}</td>
                    <td>{item.sort}</td>
                    <td>{item.active}</td>
                    <td>Update</td>
                    <td>Delete</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </form>
      </div>
    </div>
  );
};

export default Menu;
